import React, {useState, useContext} from 'react'
import { MDBRangeInput, MDBRow } from "mdbreact";

function EditSlider({content, func, value}) {
    const [range, setRange] = useState(content.paddingTop);

    const onChange = (e) => {
        setRange(e.target.value)
      }

    return (

    <MDBRangeInput
          min={0}
          max={100}
          value={50}
          formClassName="w-25"
        />
    )
}

export default EditSlider
