        }else if(urlData.length > 0){
            alert("ì´ë¯¸ ì¡´ìž¬í•˜ëŠ” urlìž…ë‹ˆë‹¤. ë‹¤ë¥¸ urlì„ ì‚¬ìš©í•´ì£¼ì„¸ìš”.");
