import React, {useState, useContext} from 'react'
import { MDBRange } from 'mdb-react-ui-kit';
import { MyContext } from '../../../../pages/Make/MakePageV2'
import produce from 'immer';

function EditSlider({content, func, value}) {
    const {state, action} = useContext(MyContext)

    const [range, setRange] = useState(content.paddingTop);

    const setPaddingTop = (e) => {
        action.setContents(produce(state.contents, draft=>{
            draft[state.secNum].paddingTop = e.target.value
        }))
    }
    const setPaddingBottom= (e) => {
        action.setContents(produce(state.contents, draft=>{
            draft[state.secNum].paddingTop = e
        }))
    }

    const onChange = (e) => {
        setRange(e.target.value)
      }

    return (
    <MDBRange
      value={value}
      id='customRange'
      label=''
      min='0'
      max='20'
      onChange={onChange}   
      onClick={e=>func(e)}
    />
    )
}

export default EditSlider
